"use strict";

// https://stackoverflow.com/questions/18699182/c-sharp-like-events-in-javascript
function createEvent() {
	var invokeList = [];

	var event = function() {
		for (var i = 0; i < invokeList.length; i++) {
			invokeList[i].apply(this, arguments);
		}
	};

	event.add = function(value) {
		invokeList[invokeList.length] = value;
	};

	return event;
}

module.exports = createEvent;
