"use strict";

const URL = {
	PROTOCOL: "http",
	HOST: "www.dream-pro.info",
	PATH: [
		"~lavalse",
		"LR2IR",
		"search.cgi"
	]
};

const baseUrl = (function createBaseUrl() {
	const { PROTOCOL, HOST, PATH } = URL;
	return `${PROTOCOL}://${HOST}/${PATH[0]}/${PATH[1]}/${PATH[2]}`;
})();

module.exports.URL = Object.freeze(URL);
