"use strict";

const user = {
	lr2id: "",
	password: ""
};

const setUser = function(lr2id, password) {
	user.lr2id = lr2id;
	user.password = password;
};

const getUserCensored = function() {
	return {
		user: user.lr2id,
		password: user.password.replace(/./g, "*")
	};
};

module.exports = {
	user: user,
	setUser: setUser,
	getUserCensored: getUserCensored
};
