"use strict";

const http = require("http");

let url = ""; // Error: Protocol "https:" not supported. Expected "http:"
let interval = 29 * 60 * 1000;
let log = console.log;
const wakeUp = function(callback) {
	http.get(url, callback);
};
let intervalID = null;

module.exports = {
	setUrl: function(newUrl) {
		url = newUrl;
		return this;
	},
	setInterval: function(newInterval) {
		interval = newInterval;
		return this;
	},
	setLog: function(newLog) {
		log = newLog;
		return this;
	},
	// TODO rename?
	start: function(options) {
		if (intervalID !== null) {
			log("Already started. Ignore.");
			return;
		}
		// TODO: setInterval -> setTimout
		intervalID = setInterval(wakeUp, interval);
	},
	stop: function() {
		clearInterval(intervalID);
		intervalID = null;
	},
	wake: wakeUp,
};

